import React, { Component } from 'react';
import { StyledNote } from './styles';
// import CREATE_NOTE from '../../query';
// import { Mutation } from 'react-apollo'


class CreateNote extends Component {
    constructor(props) {
        super(props);
        this.state = {
            title: '',
            content: '',
            author: '',
            place: '',
            notes: []
        }
    }

    handleSubmit = (e) => {
        const { title, content, author, place, } = this.state;
        e.preventDefault();
        const newItem = {
            title: title,
            content: content,
            author: author,
            place: place,
        };
        this.setState(state => ({
            notes: state.notes.concat(newItem),
            title: '',
            content: '',
            author: '',
            place: ''
        }));
    }

    handleChange = (evt) => {
        console.log(evt.target.name);
        console.log("Hello this is the value", evt.target.value)
        this.setState({ [evt.target.name]: evt.target.value });
    }

    onKeyUp = (target, event) => {
        if (event.keyCode === 13) {
            switch (target) {
                case 'title':
                    this.content.focus();
                    break;
                case 'content':
                    this.author.focus();
                    break;
                case 'author':
                    this.place.focus();
                    break;
                case 'place':
                    this.submit.focus();
                    break;
                default: console.log("Hey How Are you");

            }
        }

    }

    render() {
        const { title, content, author, place } = this.state;
        return (
            <StyledNote>
                <div className="note">
                    Title: <input
                        type="text"
                        name="title"
                        onChange={this.handleChange}
                        value={this.state.title}
                        ref={(input) => { this.title = input }}
                        onKeyUp={this.onKeyUp.bind(this, 'title')}
                    /><br />
                    Content: <input
                        type="text"
                        name="content"
                        onChange={this.handleChange}
                        value={this.state.content}
                        ref={(input) => { this.content = input }}
                        onKeyUp={this.onKeyUp.bind(this, 'content')}
                    /><br />
                    Author: <input
                        type="text"
                        name="author"
                        onChange={this.handleChange}
                        value={this.state.author}
                        ref={(input) => { this.author = input }}
                        onKeyUp={this.onKeyUp.bind(this, 'author')}
                    /><br />
                    Place: <input
                        type="text"
                        name="place"
                        onChange={this.handleChange}
                        value={this.state.place}
                        ref={(input) => { this.place = input }}
                        onKeyUp={this.onKeyUp.bind(this, 'place')}
                    /><br />

                    <button type="button"
                        ref={(input) => { this.submit = input }}
                        onKeyUp={this.onKeyUp.bind(this, 'submit')}>Submit</button>
                    {/* <Mutation mutation={CREATE_NOTE}>{
                   createNote=>(
                       <button type="button" onclick={ ()=> {
                           createNote({
                               variables:{title,content,author,place}
                           })
                       }}>SubmitNote</button>
                   )
               }
            </Mutation> */}
                </div>
            </StyledNote>
        );
    }
}

export default CreateNote;
