import React, { Component } from "react";
import { StyledUserLogin } from "./styles";
import { CREATE_NOTE } from "../../query";
import { Mutation, Query } from "react-apollo";
import ApolloClient from "apollo-boost";
import { GET_USERS } from "../../query";
import gql from "graphql-tag";
import { tsRestType } from "@babel/types";

const client = new ApolloClient({
  uri: "http://localhost:8002/"
});

export default class UserLogin extends Component {
  constructor(props) {
    super(props);
    this.state = { userName: "", password: "" };
  }
  handleUserName = event => {
    this.setState({ userName: event.target.value });
  };

  handlePassword = event => {
    this.setState({ password: event.target.value });
  };

  handleUserLogin = data => {
    const { userName, password } = this.state;
    if (
      data.getUsers.some(
        user => user.email === userName && user.password === password
      )
    ) {
      alert("User loggedin successfully");
      window.location.href = "/viewUsers";
    } else {
      alert("Please enter correct username and password");
    }
  };

  render() {
    return (
      <Query query={GET_USERS}>
        {({ loading, error, data }) => {
          if (loading) return <p>Loading...</p>;
          if (error) return <p>Error...</p>;
          if (data) {
            console.log(data);
            return (
              <StyledUserLogin>
                <div className="note">
                  Username: <input type="text" onChange={this.handleUserName} />{" "}
                  <br />
                  Password:{" "}
                  <input type="password" onChange={this.handlePassword} />{" "}
                  <br />
                  <div style={{ marginLeft: "100px" }}>
                    <button onClick={() => this.handleUserLogin(data)}>
                      Login
                    </button>
                    <button className="forget-btn" type="button">
                      Forget Password
                    </button>
                  </div>
                </div>
              </StyledUserLogin>
            );
          }
        }}
      </Query>
    );
  }
}
