import React, { Component } from 'react';
import { StyledNote } from './styles';

class CreateNote extends Component {
    constructor(props) {
        super(props);
        this.state = {
            title: '',
            content: '',
            author: '',
            place: '',
            notes: []
        }
    }
    handleSubmit(e) {
        e.preventDefault();
        if (!this.state.text.length) {
            return;
        }
        const newItem = {
            title: this.state.title,
            content: this.state.content,
            author: this.state.author,
            place: this.state.place,
        };
        this.setState(state => ({
            notes: state.notes.concat(newItem),
            title: '',
            content: '',
            author: '',
            place: ''
        }));
}

handleChange = (evt) => {
    console.log(evt.target.name);
    console.log("Hello this is the value", evt.target.value)
    this.setState({ [evt.target.name]: evt.target.value });
}
render() {
    return (
        <StyledNote>
            <div className="note">
                Title: <input 
                        type="text" 
                        name="title" 
                        onChange={this.handleChange} 
                        value={this.state.title} /><br />
                Content: <input 
                        type="text" 
                        name="content" 
                        onChange={this.handleChange} 
                        value={this.state.content} /><br />
                Author: <input 
                        type="text" 
                        name="author" 
                        onChange={this.handleChange} 
                        value={this.state.author} /><br />
                Place: <input 
                        type="text" 
                        name="place" 
                        onChange={this.handleChange} 
                        value={this.state.place} /><br />
                <button 
                    type="button" 
                    onClick={this.handleSubmit}>Submit
                </button>
                <button 
                    type="button">Reset
                </button>
            </div>
        </StyledNote>
    );
  }
}

export default CreateNote;
