import React, { Component } from 'react';
import { StyledNote } from './styles';
import CREATE_NOTE from '../../query';
import { Mutation } from 'react-apollo'


class CreateNote extends Component {
    constructor(props) {
        super(props);
        this.state = {
            title: '',
            content: '',
            author: '',
            place: '',
            notes: []
        }
    }

handleSubmit=(e) =>{
    const {title,content,author,place,}= this.state;
        e.preventDefault();
        const newItem = {
            title: title,
            content: content,
            author: author,
            place: place,
        };
        this.setState(state => ({
            notes: state.notes.concat(newItem),
            title: '',
            content: '',
            author: '',
            place: ''
        }));
}

handleChange = (evt) => {
    console.log(evt.target.name);
    console.log("Hello this is the value", evt.target.value)
    this.setState({ [evt.target.name]: evt.target.value });
}

render() {
    console.log("this is the number of items",this.state.notes)
    const {title,content,author,place}= this.state;
    console.log("Hey -------->>>>>>",title,place,content,author)
    return (
        <StyledNote>
            <div className="note">
                Title: <input 
                        type="text" 
                        name="title" 
                        onChange={this.handleChange} 
                        value={this.state.title} /><br />
                Content: <input 
                        type="text" 
                        name="content" 
                        onChange={this.handleChange} 
                        value={this.state.content} /><br />
                Author: <input 
                        type="text" 
                        name="author" 
                        onChange={this.handleChange} 
                        value={this.state.author} /><br />
                Place: <input 
                        type="text" 
                        name="place" 
                        onChange={this.handleChange} 
                        value={this.state.place} /><br />
               <Mutation mutation={CREATE_NOTE}>{
                   createNote=>(
                       <button type="button" onclick={ ()=> {
                           createNote({
                               variables:{title,content,author,place}
                           })
                       }}>SubmitNote</button>
                   )
               }
                </Mutation>
            </div>
        </StyledNote>
    );
  }
}

export default CreateNote;
