import React from 'react';
import logo from './logo.svg';
import './App.css';
import {
  Query,
} from 'react-apollo';
import {gql} from 'apollo-boost'


const getNotes = gql`
   query getNotes{
    content
    title
  }
   }
 `;


class App extends React.Component {
  render() {
    return (
      <Query query={getNotes}>
        {
          ({ loading, error, data }) => {
            if (loading) return <p>loading.....</p>;
            if (error) return <p>error</p>
            console.log(data);
          }
        }
      </Query>
    );
  }
}

export default App;
