import React, { Component } from "react";
import { StyledUserLogin } from "./styles";
import { CREATE_NOTE } from "../../query";
import { Mutation, Query } from "react-apollo";
import ApolloClient from "apollo-boost";
import {USER_LOGIN} from '../../query';
import gql from "graphql-tag";



// const client = new ApolloClient({
//   uri: "http://localhost:8002/"
// });

export default class UserLogin extends Component {
  constructor(props){
    super(props);
    this.state = {userName:'', password:''}
  }
  handleUserName=(event)=> {
    this.setState({userName: event.target.value})
  }

  handlePassword =(event) =>{
    this.setState({password: event.target.value})
  }
  // componentDidMount(){
  //   this.handleUserLogin();
  // }

  render() {
    const {userName,password} = this.state;
    return (
      <StyledUserLogin>
        <div className="note">
          Username: <input type="text" onChange={this.handleUserName} /> <br />
          Password: <input type="password" onChange={this.handlePassword} /> <br />
          <div style={{marginLeft:'100px'}}>
            <Query query={USER_LOGIN}>
              {({loading,error,data}) => {
                if(loading) return <p>Checkin permission</p>;
                if(error) return <p> Something went wrong </p>;
                if(data){
                  console.log("hellooooooooooooo",data);
                }
              }}
            </Query>
            <button className="forget-btn" type="button">
              Forget Password
            </button>
            </div>
        </div>
      </StyledUserLogin>
    );
  }
}
