import React, { Component } from "react";
import { Query } from "react-apollo";
import GET_BOOKS from "../../query";
import { StyledTable } from "./styles";

class ViewAllNotes extends Component{
  render() {
    return(
      <Query query={GET_BOOKS}>
        {({ loading, error, data }) => {
          if (loading) return <p>Loading...</p>;
          if (error) return <p>Bye Bye Amit Arya...</p>;
          console.log(data.getNotes);
          if(data)
            return(
              <React.Fragment>
                <StyledTable>
                  <table className="table">
                    <tr>
                      <th>Title</th>
                      <th>Content</th>
                      <th>Author</th>
                      <th>Place</th>
                      <th>Action</th>
                    </tr>
                    {data.getNotes.map(note => (
                      <>
                        <tr>
                          <td>{note.title}</td>
                          <td>{note.content}</td>
                          <td>{note.author}</td>
                          <td>{note.place}</td>
                          <td>
                            <button>Edit</button>
                            <button>Delete</button>
                          </td>
                        </tr>
                      </>
                    ))}
                  </table>
                </StyledTable>
              </React.Fragment>
            );
            }
            }
      </Query>
    );
  }
}

