import React, { Component } from 'react';
import {StyledNote} from './styles';

class CreateNote extends Component {
    constructor(props){
        super(props);
        this.state = {
            title: '',
            content: '',
            author: '',
            place:'',
            note: []
        }
    }

    handleChange =(evt)=>{
        console.log(evt.target.name);
        console.log("Hello this is the value",evt.target.value)
        this.setState({[evt.target.name]: evt.target.value});
    }
    render() {
        return (
            <StyledNote>
            <div className="note">
                Title: <input type="text" name="title" onChange={this.handleChange}/><br />
                Content: <input type="text" name="content" onChange={this.handleChange}/><br />
                Author: <input type="text" name="author" onChange={this.handleChange} /><br />
                Place: <input type="text" name="place" onChange={this.handleChange} /><br />
                <button type="button">Submit</button>
                <button type="button">Reset</button>
            </div>
            </StyledNote>
        );
    }
}

export default CreateNote;
