import React from 'react';
import ApolloClient from "apollo-boost";
import { ApolloProvider, Query } from "react-apollo";
import gql from "graphql-tag";

const client = new ApolloClient({
    uri: "http://localhost:4000/"
});

const GET_BOOKS = gql`
{
    getNotes {
        content
        title
    }
}`;

const GetNotes = () => (
    <Query query={GET_BOOKS}>
        {
            ({ loading, error, data }) => {
                if (loading) return <p>Loading...</p>;
                if (error) return <p>Bye Bye Amit Arya...</p>
                console.log(data)
                if (data) return (
                    <React.Fragment>
                            <table>
                                <tr>
                                    <th>Title</th>
                                    <th>Content</th>
                                </tr>
                                {
                                    data.getNotes.map((note)=> (
                                        <>
                                         <tr>
                                             <td>{note.title}</td>
                                             <td>{note.content}</td>
                                         </tr>
                                        </>
                                    ))
                                }
                            </table>
                </React.Fragment>
                        )
                    }}

    </Query>
                )

                const App = () => (
                    <ApolloProvider client={client}>
                        <div>
                            <h2>My First Apollo Client App</h2>
                            <GetNotes />
                        </div>
                    </ApolloProvider>
                );

                export default App;
