import React, { Component } from "react";
import { StyledUserLogin } from "./styles";
import { Query } from "react-apollo";
import { USER_LOGIN } from "../../query";
import { Mutation } from 'react-apollo'


export default class UserLogin extends Component {
  constructor(props) {
    super(props);
    this.state = { name: "", password: "" };
  }
  handleUserName = event => {
    this.setState({ name: event.target.value });
  };

  handlePassword = event => {
    this.setState({ password: event.target.value });
  };

  handleSubmit = () => {
    this.setState(state => ({
        name: '',
        password: '',
    }));
}

  // handleSubmit = data => {
  //   console.log("login user", data)
  //   const { userName, password } = this.state;
  //   if (
  //     data.getUsers.some(
  //       user => user.name === userName && user.password === password
  //     )
  //   ) {
  //     alert("User loggedin successfully");
  //     window.location.href = "/viewUsers";
  //   } else {
  //     alert("Please enter correct username and password");
  //   }
  // };

  render() {
    const {userName,password} = this.state;    
    console.log("Hello---------------->>>>",userName);
    console.log("Hello------------->>>>>>>>>.",password);  
            return (
              <StyledUserLogin>
                <div className="note">
                  Username: <input type="text" onChange={this.handleUserName} />{" "}
                  <br />
                  Password:{" "}
                  <input type="password" onChange={this.handlePassword} />{" "}
                  <br />
                  <div style={{ marginLeft: "100px" }}>
                  <Mutation mutation={USER_LOGIN}>{
                        userLogin => (
                            <button type="button"
                                className="submit-btn"
                                ref={(input) => { this.login = input }}
                                disabled={!(userName && password)}
                                onClick={() => {
                                    this.handleSubmit();
                                    // console.log(userLogin,password)
                                    userLogin({ variables: { userName, password } })
                                        .then(res => console.log("hellloooooooooo---------",res))
                                        .catch(err => console.log("Error", err))
                                }}>Login</button>
                        )
                    }
                    </Mutation>
                    <button className="forget-btn" type="button">
                      Forget Password
                    </button>
                  </div>
                </div>
              </StyledUserLogin>
        );
    }
}
